code:
{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "description": "An example of a circle packing layout for hierarchical data.",
  "width": "[ChartSize]",
  "height": "[ChartSize]",
  "padding": 5,
  "autosize": "none",

  "data": [
        {
      "name": "names",
      "values": "[Pivots]",
      "transform": [
        {"type": "project", "fields": ["data"], "as": ["key"]},
        {"type": "window", "ops": ["row_number"], "as": ["id"]}
      ]
    },
    {
      "name": "tree",
            "values": "myData",
      "transform": [
        {"type": "nest", "keys": "[Pivots]", "generate": true},
        {
          "type": "pack",
                    "field": {
            "$cond": {
              "query": "parameters.Value",
              "true": "[Value]"
            }
          }, 
          "sort": {"field": "value"},
          "size": [{"signal": "width"}, {"signal": "height"}]
        },
                {"type": "filter", "expr": "datum.children"},
        {
          "type": "lookup",
          "from": "names",
          "key": "id",
          "fields": ["depth"],
          "values": ["key"],
          "as": ["tipper"]
        },
        {
          "type": "formula",
          "expr": "{field: datum.tipper, value: datum.key, children: datum.children}",
          "as": "tooltip"
        }
      ]
    }
  ],

  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "tree", "field": "depth"},
      "range": {"scheme": "[Color]"}
    }
  ],

  "marks": [
    {
      "type": "symbol",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "shape": {"value": "circle"},
          "fill": {"scale": "color", "field": "depth"},
          "tooltip": {"field": "tooltip"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "size": {"signal": "4 * datum.r * datum.r"},
          "stroke": {"value": "white"},
          "strokeWidth": {"value": 0.5}
        },
        "hover": {
          "stroke": {"value": "red"},
          "strokeWidth": {"value": 2}
        }
      }
    }
  ]
}
